Start Background Music<pre><code>      private void FrmLevel_Load(object sender, EventArgs e) {
      const int PADDING = 7;
      const int NUM_WALLS = 13;
      MusicSettings.PlayBackgroundMusic(); // start in-game music
</code></pre>

Stop Background Music<pre><code>    public void SetupForBossBattle() {

      picBossBattle.Location = Point.Empty;
      picBossBattle.Size = ClientSize;
      picBossBattle.Visible = true;

      MusicSettings.StopBackgroundMusic(); //stop background music
      SoundPlayer simpleSound = new SoundPlayer(Resources.final_battle);
      simpleSound.Play();// play intro audio for BossBattle

      tmrFinalBattle.Enabled = true;
    }
</code></pre> Restart Background Music<pre><code>    private void tmrFinalBattle_Tick(object sender, EventArgs e) {
      picBossBattle.Visible = false;
      tmrFinalBattle.Enabled = false;
      MusicSettings.PlayBackgroundMusic();  //restart background music

    }
</code></pre>
